package com.class2;

public class StringConcatenation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String name1=" John";
		//My name is John;
		
		System.out.println("My name is"+name1);
		
		
		String day="Sunday";
		//Sunday is a coding day
		
		System.out.println(day +" is a coding day");
		
		
		int num1=12;
		int num2=13;
		//The value of num1 is __ and the value of num2 is __.
		
		System.out.println("The value of num1 is " + num1 + " and the value of num2 is " +num2+ "." );
		
		
		//*
		//*
		String name="Adrian";
		String lastName="Cristian";
		char grade='A';
		String city="Washington";
		String state="District Of Columbia";
		long phoneNumber=9297771761L;
		
		
		System.out.println("My name is " +name+ " and my last name is " +lastName+ ". I am a " +grade+ " grade student. I live in the city of " +city+ ", in the state of " +state+ ". My phone number is " +phoneNumber+ ".");
		
		
		//*
		System.out.println("My name is " +name+ " and my last name is " +lastName+ ".");
		System.out.println("I am a " +grade+ " grade student.");
		System.out.println("I live in the city of " +city+ ", in the state of " +state+ ".");
		System.out.println("My phone number will be " +phoneNumber+ ".");
		
		
		
	}

}
